import React, { Component, Fragment } from 'react'
import XiaojiejieItem from './XiaojiejieItem'
import axios from 'axios'
import './style.css'

class Xiaojiejie extends Component {
    constructor(props) {
        super(props) //调用父类的构造函数，固定写法
        this.state = {
            inputValue: '', // input中的值
            list: ['头部按摩', '精油推背', '足底按摩']    //服务列表
        }
    }


    inputChange() {
        this.setState({
            inputValue: this.input.value
        })
    }

    addList() {
        this.setState({
            list: [...this.state.list, this.state.inputValue],
            inputValue: ' '
        })

        console.log(this.ul.querySelectorAll('div').length)

    }

    deleteItem(index) {
        let list = this.state.list
        list.splice(index, 1)
        this.setState({
            list: list
        })
    }

    render() {
        return (
            <Fragment>
                <label htmlFor='tony'>加入服务：</label>
                <input 
                    id='tony' 
                    className='input' 
                    value={this.state.inputValue} 
                    onChange={this.inputChange.bind(this)} 
                    ref={(input)=>{this.input=input}}
                />
                <button onClick={this.addList.bind(this)}> 增加服务 </button>
                <ul ref={(ul)=>{this.ul=ul}}>
                    {
                        this.state.list.map((item, index) => {
                            return (
                                <XiaojiejieItem
                                    key={index + item}
                                    content={item}
                                    index={index} 
                                    deleteItem={this.deleteItem.bind(this)}   
                                />

                            )
                        })
                    }
                </ul>
            </Fragment>
        )
    }

    componentDidMount() {
        axios.post('https://www.easy-mock.com/mock/5dd34bb6a881483f4735b865/react/message')
            .then((res)=>{
                console.log('axios获取成功:' + JSON.stringify(res))
            })
            .catch((error)=>{
                console.log('axios获取数据失败' + error)
            })
    }



}

export default Xiaojiejie
